                    <Popover class="relative" v-slot="{ open }">
                      <template v-if="(itemIdx === 2)">
                        <PopoverButton class="bg-gray-200 text-gray-900 px-3 py-2 rounded-md text-sm font-medium focus:outline-none">
                          <span>{{ item }}</span>
                        </PopoverButton>
                      </template>
                      <PopoverButton v-else :class="[open ? 'bg-gray-200 text-gray-900' : 'text-gray-600', 'group hover:bg-gray-100 hover:bg-opacity-75 px-3 py-2 rounded-md text-sm font-medium focus:outline-none']">
