                            <div class="relative grid gap-6 bg-white px-5 py-6 sm:gap-8 sm:p-8">
                              <a v-for="item2 in solutions" :key="item2.name" :href="item2.href" class="-m-3 p-3 flex items-start rounded-lg hover:bg-gray-50">
