                                <div class="ml-4">
                                  <p class="text-base font-medium text-gray-900">
                                    {{ item2.name }}
                                  </p>
                                  <p class="mt-1 text-sm text-gray-500">
                                    {{ item2.description }}
                                  </p>
                                </div>
